package Week1.day1;

public class EvenNumReverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Printing even number in reverse order

		for(int num=20;num>0;num--) {
			if(num%2==0) {
				System.out.println(num+ " is a Even number");
			}
		}
	}

}
